DROP TABLE IF EXISTS `ems_employee_worker`;
CREATE TABLE `ems_employee_worker` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT COMMENT '编号',
  `name` varchar(50) NOT NULL DEFAULT '' COMMENT '姓名',
  `idcard` varchar(50) DEFAULT NULL COMMENT '身份证号',
  `phone` varchar(30) DEFAULT NULL COMMENT '电话',
  `factory` varchar(255) DEFAULT NULL COMMENT '工厂',
  `categoryid` int(11) DEFAULT '1' COMMENT '员工类别',
  `deliverydate` int(10) unsigned DEFAULT NULL COMMENT '送厂日期',
  `terminationdate` int(10) unsigned DEFAULT NULL COMMENT '合同终止日期',
  `undercontractwage` decimal(12,2) NOT NULL DEFAULT '0.00' COMMENT '未满协议工价',
  `fullagreementwage` decimal(12,2) NOT NULL DEFAULT '0.00' COMMENT '满协议工价',
  `originid` int(11) unsigned DEFAULT '0' COMMENT '来源工头id',
  `commission` decimal(12,4) NOT NULL DEFAULT '0.0000' COMMENT '工头提成',
  `isinservice` varchar(1) DEFAULT 'Y' COMMENT '是否在职',
  `addedinfo` varchar(255) DEFAULT NULL COMMENT '备注',
  PRIMARY KEY (`id`),
  KEY `idcard` (`idcard`)
) ENGINE=MyISAM AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ems_employee_worktime`;
CREATE TABLE `ems_employee_worktime` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '工时id',
  `time` int(10) unsigned DEFAULT NULL COMMENT '年月',
  `workerid` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '员工id',
  `checkindate` int(10) unsigned DEFAULT NULL COMMENT '报到日期',
  `leavedate` int(10) unsigned DEFAULT NULL COMMENT '离职日期',
  `price` decimal(12,2) NOT NULL DEFAULT '0.00' COMMENT '单价',
  `totaltime` decimal(12,2) NOT NULL DEFAULT '0.00' COMMENT '总工时',
  `waterandelectricity` decimal(12,2) NOT NULL DEFAULT '0.00' COMMENT '水电',
  `overallcost` decimal(12,2) NOT NULL DEFAULT '0.00' COMMENT '工衣',
  `iccard` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT 'IC卡',
  `penalty` decimal(12,2) NOT NULL DEFAULT '0.00' COMMENT '罚款',
  `commercialinsurance` decimal(12,2) NOT NULL DEFAULT '0.00' COMMENT '商保',
  `managementcost` decimal(12,2) NOT NULL DEFAULT '0.00' COMMENT '管理费',
  `carfare` decimal(12,2) NOT NULL DEFAULT '0.00' COMMENT '车费',
  `borrow` decimal(12,2) NOT NULL DEFAULT '0.00' COMMENT '借资',
  `liquidateddamages` decimal(12,2) NOT NULL DEFAULT '0.00' COMMENT '违约金',
  `isagreement` varchar(1) NOT NULL DEFAULT 'Y' COMMENT '是否满协议',
  `addedinfo` varchar(255) DEFAULT NULL COMMENT '备注',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;


DROP TABLE IF EXISTS `ems_report_tables`;
CREATE TABLE `ems_report_tables` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `text` varchar(255) DEFAULT NULL,
  `type` varchar(255) DEFAULT NULL,
  `grf` longtext COMMENT '报表文件',
  `dataurl` varchar(255) DEFAULT '',
